mapper.insert:
insert into fund_history_nav
(code, nav_date, nav)
select #{code}, #{navDate}, #{nav}
from dual
where not exists (
select nav_date
from fund_history_nav
where code = #{code} and nav_date = #{navDate}
)

mapper.select:
select code, nav_date, nav
from fund_history_nav

    and code = #{code}
    and nav_date = #{navDate}

select code, nav_date, nav from fund_history_nav
and code = #{code}
order by nav_date desc limit 1
