mapper.select:
select code, total_amount, total_purchase_fee, held_share
from (
select code, total_amount, total_purchase_fee, held_share,
row_number() over (partition by code order by held_share desc) as rn
from fund_position
where code = #{code}
) tmp
where rn = 1

mapper.update:
update fund_position
held_days = #{heldDays}, update_date = #{updateDate},
where code = #{code} and transaction_date = #{transactionDate} and initiation_date = #{initiationDate} and total_amount = #{totalAmount} and total_purchase_fee = #{totalPurchaseFee} and held_share = #{heldShare}
