mapper.select:
select code, transaction_date, initiation_date, total_amount, total_purchase_fee, held_share, held_days, update_date from fund_position
and code = #{code}
order by transaction_date asc

select
code, transaction_date, initiation_date, total_amount, total_purchase_fee, held_share, held_days, update_date
from fund_position

    and code = #{code}
    and transaction_date > #{transactionDate}

order by transaction_date asc

select code, total_amount, total_purchase_fee, held_share
from (
select code, total_amount, total_purchase_fee, held_share,
row_number() over (partition by code order by held_share desc) as rn
from fund_position

    and code = #{code}
    and transaction_date < #{transactionDate}

) tmp
where rn = 1

select code, total_amount, total_purchase_fee, held_share
from (
select code, total_amount, total_purchase_fee, held_share,
row_number() over (partition by code order by held_share desc) as rn
from fund_position

    and code = #{code}
    and transaction_date = #{transactionDate}


) tmp
where rn = 1

mapper.update:
update fund_position
held_days = #{heldDays}, update_date = #{updateDate},
where code = #{code} and transaction_date = #{transactionDate} and initiation_date = #{initiationDate} and total_amount = #{totalAmount} and total_purchase_fee = #{totalPurchaseFee} and held_share = #{heldShare}

update fund_position
total_amount = #{totalAmount}, total_purchase_fee = #{totalPurchaseFee}, held_share = #{heldShare},
where code = #{code} and transaction_date = #{transactionDate} and initiation_date = #{initiationDate}
