mapper.select:
select code, total_principal_amount, total_amount, total_purchase_fee, held_share
from (
select code, total_principal_amount, total_amount, total_purchase_fee, held_share,
row_number() over (partition by code order by held_share desc) as rn
from fund_position

    and code = #{code}
    and transaction_date <= #{transactionDate}
    and mark is null

) temp
where rn = 1

select
code, transaction_date, total_amount, held_share
from fund_position

    and code = #{code}
    and transaction_date = #{transactionDate}
    and (mark is null or mark = '')

mapper.update:
update fund_position

    status = #{status},

where code = #{code} and transaction_date = #{transactionDate} and held_share = #{heldShare}

update fund_position
held_days = #{heldDays}, update_date = #{updateDate},
where code = #{code} and transaction_date = #{transactionDate} and initiation_date = #{initiationDate} and total_principal_amount = #{totalPrincipalAmount} and total_purchase_fee = #{totalPurchaseFee} and held_share = #{heldShare} and trading_platform = #{tradingPlatform}

update fund_position
redemption_date = #{redemptionDate}, status = #{status}, mark = #{mark}, total_redemption_fee = #{totalRedemptionFee}, total_amount = #{totalAmount},
where id = #{id}

update fund_position
total_principal_amount = #{totalPrincipalAmount}, held_share = #{heldShare}, total_amount = #{totalAmount}, total_purchase_fee = #{totalPurchaseFee},
where id = #{id}

update fund_position
total_redemption_fee = #{totalRedemptionFee}, total_amount = #{totalAmount},
where code = #{code} and transaction_date = #{transactionDate} and initiation_date = #{initiationDate}
