mapper.update:
update fund_purchase_transaction

    status = #{status},

where code = #{code} and transaction_date = #{transactionDate}

update fund_purchase_transaction
nav = #{nav}, share = #{share},
where code = #{code} and transaction_date = #{transactionDate} and amount = #{amount}

update fund_purchase_transaction

    mark = #{mark}

where code = #{code} and transaction_date between #{startDate} and #{endDate} and confirmation_date < #{endDate}
