mapper.update:
update fund_transaction

    status = #{status},

where code = #{code} and transaction_date = #{transactionDate}

update fund_transaction
nav = #{nav}, share = #{share},
where code = #{code} and transaction_date = #{transactionDate}

update fund_transaction
nav = #{nav}, fee = #{fee}, amount = #{amount},
where code = #{code} and transaction_date = #{transactionDate}

update fund_transaction

    mark = #{mark}

where code = #{code} and transaction_date between #{startDate} and #{endDate} and (mark is null or mark = '')
